// Setup scene, camera and renderer
const scene = new THREE.Scene();
const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
const renderer = new THREE.WebGLRenderer();
renderer.setSize(window.innerWidth, window.innerHeight);
document.body.appendChild(renderer.domElement);

// Thumbnails data (path, position)
const thumbnails = [
  { path: 'data:image/jpeg;base64,', position: { x: -2, y: 0, z: -5 } },
  { path: 'data:image/jpeg;base64,', position: { x: 0, y: 0, z: -5 } },
  { path: 'data:image/jpeg;base64,', position: { x: 2, y: 0, z: -5 } }
  
];

// Load thumbnails
const thumbnailObjects = [];
const loader = new THREE.TextureLoader();

thumbnails.forEach((thumbnailData, index) => {
  const texture = loader.load(thumbnailData.path);
  const material = new THREE.MeshBasicMaterial({ map: texture });
  const geometry = new THREE.PlaneGeometry(2, 2);
  const thumbnail = new THREE.Mesh(geometry, material);
  thumbnail.position.set(thumbnailData.position.x, thumbnailData.position.y, thumbnailData.position.z);
  thumbnail.userData.index = index; // Add index for identification
  thumbnailObjects.push(thumbnail);
  scene.add(thumbnail);
});

// Click event handler for thumbnails
function onThumbnailClick(event) {
  const intersectedObject = getIntersectedThumbnail(event.clientX, event.clientY);
  if (intersectedObject) {
    const index = intersectedObject.userData.index;
    console.log("Clicked on thumbnail", index);
    // You can perform actions like displaying a larger version of the image, etc.
  }
}

// Get intersected thumbnail object
function getIntersectedThumbnail(mouseX, mouseY) {
  const raycaster = new THREE.Raycaster();
  const mouse = new THREE.Vector2();
  mouse.x = (mouseX / window.innerWidth) * 2 - 1;
  mouse.y = -(mouseY / window.innerHeight) * 2 + 1;
  raycaster.setFromCamera(mouse, camera);
  const intersects = raycaster.intersectObjects(thumbnailObjects);
  return intersects.length > 0 ? intersects[0].object : null;
}

// Event listener for mouse click on thumbnails
window.addEventListener('click', onThumbnailClick, false);

// Position the camera
camera.position.z = 5;

// Render loop
function animate() {
  requestAnimationFrame(animate);
  thumbnailObjects.forEach(thumbnail => {
    thumbnail.rotation.y += 0.01; // Rotate around vertical axis
  });
  renderer.render(scene, camera);

  renderer.render(scene, camera);
}
animate();
